import React, { Component } from 'react';
export default class About extends Component {
  render() {
    let siteData = this.props.siteData;
    return (
      <section id="about">
         <div className="row">

            <div className="nine columns main-col">

               <h2>Who Jim Was:</h2>
               <p>
               {
                 siteData.aboutme
               }
               </p>

               <div className="row">

                  <div className="columns contact-details">

                  <h2>His Journey</h2>
                  <p className="address">
       						<span>{siteData.name}</span>
                     <br></br>
       						   <span>
                     {siteData.address}
                    </span>
                    <br></br>
                    <span>{siteData.website}</span>
       					   </p>
                  </div>
               </div>

               <div className="row banner">
            <div className="banner-text">
               <h1 className="responsive-headline">{siteData.name}</h1>
               <h3 style={{color:'#fff', fontFamily:'sans-serif '}}>{siteData.role}{siteData.roleDescription}
               </h3>
               <hr/>
               <ul className="youtube-link">
                  {
                    siteData.journey && siteData.youtubeLinkLinks.map(item =>{
                      return(
                              <li key={item.name}>
                                <a href={item.url} target="_blank"><i className={item.className}></i></a>
                              </li>
                            )
                          }
                    )
                  }
               </ul>
            </div>
         </div>

            </div>
         </div>
      </section>
    );
  }
}
